# coding: utf-8

# 色を変更させる箇所を抽出
# 探すのは4パターンのみ。
# @param [Array] array キャラクターの並びの配列
# @return [Hash] indices arrayのインデックスと、変更結果の色のハッシュ
def search array
  indices = {}
  (0..array.size-4).each do |s|
    subarray = array[s..(s+3)]
    # Pattern 1
    if subarray[0] != subarray[1] && subarray[1] == subarray[2] && subarray[1] == subarray[3]
      indices[s] = subarray[1]
    end
    # Pattern 2
    if subarray[0] != subarray[1] && subarray[0] == subarray[2] && subarray[0] == subarray[3]
      indices[s+1] = subarray[0]
    end
    # Pattern 3
    if subarray[0] == subarray[1] && subarray[0] != subarray[2] && subarray[0] == subarray[3]
      indices[s+2] = subarray[0]
    end
    # Pattern 4
    if subarray[0] == subarray[1] && subarray[0] == subarray[2] && subarray[0] != subarray[3]
      indices[s+3] = subarray[0]
    end
  end
  indices
end

# 消える箇所を確定させる
# 1色変えるだけなので、最大発火箇所は1カ所のみ
# @param [Array] array 配列
# @return [Array] 開始インデックス、終了インデックス、色
# @return [Nil] 削除対象がなかった場合
def check array
  start_index = 0
  prev_color  = 0
  array.each_with_index do |color, i|
    unless prev_color == color
      # 変わった瞬間に確定
      if (i-1) - start_index >= 3
        return [start_index, i-1, prev_color]
      end
      start_index = i
    end
    prev_color = color
  end
  # 変わらないまま長さが伸びているケースに対応
  if (array.size - 1) - start_index >= 3
    [start_index, array.size-1, prev_color]
  else
    [nil, nil, nil]
  end
end

def chain a
  start, last, color = check a
  if start
    a.slice!(start, last-start+1)
    chain(a)
  else
    a
  end
end

